pragma solidity =0.8.18;

import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';

/**
 * @title Staking
 * @author kotsmile
 */
contract Staking {
    using SafeERC20 for IERC20;

    struct Stake {
        bool isStaked;
        uint256 id;
        uint256 amount;
        uint256 reward;
        uint256 timestamp;
    }

    IERC20 public token;

    uint256[] public periods = [30, 60, 90];
    uint256[] public apy = [120_00, 150_00, 180_00];
    uint256 public constant DENOMINATOR = 100_00; // 100%

    uint256 public stakedAmount;
    uint256 public reservedRewards;

    mapping(address => Stake) public users;

    constructor(IERC20 _token) {
        token = _token;
    }

    function stake(uint256 amount_, uint256 id) external {
        require(id < periods.length, 'Staking: invalid id');
        require(!users[msg.sender].isStaked, 'Staking: already staked');

        uint256 amount = _receiveTokens(amount_);
        uint256 reward = (amount * apy[id]) / DENOMINATOR - amount;

        require(rewardPool() >= reward, 'Staking: not enough rewards');

        stakedAmount += amount;
        reservedRewards += reward;

        Stake memory stake_ = Stake(true, id, amount, reward, block.timestamp);
        users[msg.sender] = stake_;
    }

    function unstake() external {
        require(users[msg.sender].isStaked, 'Staking: not staked');
        require(
            users[msg.sender].timestamp + periods[users[msg.sender].id] <
                block.timestamp,
            'Staking: not ready'
        );

        uint256 reward = users[msg.sender].reward;
        uint256 amount = users[msg.sender].amount;

        stakedAmount -= amount;
        reservedRewards -= reward;

        token.safeTransfer(msg.sender, amount + reward);

        delete users[msg.sender];
    }

    function _receiveTokens(uint256 amount) internal returns (uint256) {
        uint256 balanceBefore = token.balanceOf(address(this));
        token.safeTransferFrom(msg.sender, address(this), amount);
        uint256 balanceAfter = token.balanceOf(address(this));
        return balanceAfter - balanceBefore;
    }

    function rewardPool() public view returns (uint256) {
        return token.balanceOf(address(this)) - stakedAmount - reservedRewards;
    }

    function getUserStake(address user) external view returns (Stake memory) {
        return users[user];
    }

    function legitMaxId() external view returns (uint256) {
        return periods.length;
    }
